mapper.select:
        SELECT
        

--         级联表的字段

        FROM peijian  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.peijian_uuid_number like CONCAT('%',#{params.peijianUuidNumber},'%')
            
            
                and a.peijian_name like CONCAT('%',#{params.peijianName},'%')
            
            
                and a.peijian_types = #{params.peijianTypes}
            
            
                  and a.peijian_kucun_number >= #{params.peijianKucunNumberStart}   
            
            
                  and a.peijian_kucun_number <= #{params.peijianKucunNumberEnd}   
            
             
                and a.peijian_kucun_number = #{params.peijianKucunNumber}
             
            
                  and a.peijian_new_money >= #{params.peijianNewMoneyStart}   
            
            
                  and a.peijian_new_money <= #{params.peijianNewMoneyEnd}   
            
            
                and a.peijian_content like CONCAT('%',#{params.peijianContent},'%')
            
            
                  and a.peijian_delete >= #{params.peijianDeleteStart}   
            
            
                  and a.peijian_delete <= #{params.peijianDeleteEnd}   
            
             
                and a.peijian_delete = #{params.peijianDelete}
             

        

        order by a.${params.orderBy} desc
