mapper.select:
        SELECT
        

--         级联表的字段

        FROM weixiuxiangmu  a

        
            
                and a.id in
                
                #{item}
                
            
            
                and a.weixiuxiangmu_name like CONCAT('%',#{params.weixiuxiangmuName},'%')
            
            
                and a.weixiuxiangmu_types = #{params.weixiuxiangmuTypes}
            
            
                  and a.weixiuxiangmu_new_money >= #{params.weixiuxiangmuNewMoneyStart}   
            
            
                  and a.weixiuxiangmu_new_money <= #{params.weixiuxiangmuNewMoneyEnd}   
            
            
                  and a.weixiuxiangmu_clicknum >= #{params.weixiuxiangmuClicknumStart}   
            
            
                  and a.weixiuxiangmu_clicknum <= #{params.weixiuxiangmuClicknumEnd}   
            
             
                and a.weixiuxiangmu_clicknum = #{params.weixiuxiangmuClicknum}
             
            
                and a.weixiuxiangmu_content like CONCAT('%',#{params.weixiuxiangmuContent},'%')
            
            
                and a.shangxia_types = #{params.shangxiaTypes}
            
            
                  and a.weixiuxiangmu_delete >= #{params.weixiuxiangmuDeleteStart}   
            
            
                  and a.weixiuxiangmu_delete <= #{params.weixiuxiangmuDeleteEnd}   
            
             
                and a.weixiuxiangmu_delete = #{params.weixiuxiangmuDelete}
             

        

        order by a.${params.orderBy} desc
